CASE state OF
	
1:
PowerX(
	Axis:=MotorExample_Motor , 
	Enable:=TRUE , 
	bRegulatorOn:=TRUE , 
	bDriveStart:=TRUE , 
	Status=> , 
	bRegulatorRealState=> , 
	bDriveStartRealState=> , 
	Busy=> , 
	Error=> , 
	ErrorID=> );
	
	IF PowerX.bDriveStartRealState THEN
		Xonb:=TRUE;
		state:=state+1;
		ELSE
			state:=ERROR;
	END_IF
	
2:
HomeX(
	Axis:=MotorExample_Motor , 
	Execute:=TRUE, 
	Position:=0.0, 
	Done=> , 
	Busy=> , 
	CommandAborted=> , 
	Error=> , 
	ErrorID=> );
		
	IF HomeX.Done THEN
		Referenced:=TRUE;
		state:=state+1;
		HomeX.Execute:=FALSE;
		ELSIF HomeX.Error OR Xerrorstop OR MotorExample_Motor.bError THEN
		state:=ERROR;
	END_IF
	
3:
ExtendX.Execute:=TRUE; 
ExtendX.Distance:=Distance; 
ExtendX.Velocity:=100; 
ExtendX.Acceleration:=1000; 
ExtendX.Deceleration:=1000; 
ExtendX.Jerk:=0; 

	IF ExtendX.Done THEN
		state:=state+1;
		ExtendX.Execute:=FALSE;
		ELSIF ExtendX.Error OR Xerrorstop OR MotorExample_Motor.bError THEN
		state:=ERROR;	
	END_IF
	
4:
Wait.IN:=TRUE;
Wait.PT:=Wait_time;
Elapsed_time:=Wait.ET;

	IF Wait.Q THEN
		state:=state+1;
		Wait.IN:=FALSE;
	END_IF

5: 
RetractX.Execute:=TRUE; 
RetractX.Position:=0; 
RetractX.Velocity:=100;
RetractX.Acceleration:=1000;
RetractX.Deceleration:=1000; 
RetractX.Jerk:=0; 

IF RetractX.Done THEN
		state:=state+1;
		RetractX.Execute:=FALSE;
		counter:=counter+1;
		ELSIF RetractX.Error OR Xerrorstop THEN
		state:=ERROR;
	END_IF
	
6: 
Wait.IN:=TRUE; 
Wait.PT:=Wait_time;
Elapsed_time:=Wait.ET;

	IF Wait.Q THEN
		state:=3;
		Wait.IN:=FALSE;
	END_IF
	
ERROR:
ResetX.Execute:=TRUE;
Wait.IN:=TRUE;
IF Wait.Q THEN
	ResetX.Execute:=FALSE;
	state:=1;
END_IF

END_CASE
ExtendX(Axis:=MotorExample_Motor);
Wait();
RetractX(Axis:=MotorExample_Motor);
ResetX(Axis:=MotorExample_Motor);
ReadAxisState(Axis:=MotorExample_Motor,Enable:=TRUE,ErrorStop=>Xerrorstop);

